import React, { useState } from 'react';

function Budget() {
  // Initial data for budgets representing user transactions
  const [budgetData, setBudgetData] = useState([
    { id: 1, from: 'Account A', to: 'Account B', amount: 1000, currency: 'USD' },
    { id: 2, from: 'Account C', to: 'Account D', amount: 500, currency: 'EUR' },
    { id: 3, from: 'Account B', to: 'Account A', amount: 2000, currency: 'GBP' },
    { id: 4, from: 'Account E', to: 'Account F', amount: 1500, currency: 'INR' },
    { id: 5, from: 'Account D', to: 'Account C', amount: 1200, currency: 'AUD' },
    { id: 6, from: 'Account F', to: 'Account A', amount: 900, currency: 'JPY' },
    { id: 7, from: 'Account A', to: 'Account C', amount: 700, currency: 'USD' },
    { id: 8, from: 'Account B', to: 'Account F', amount: 3000, currency: 'EUR' },
    { id: 9, from: 'Account D', to: 'Account B', amount: 2200, currency: 'GBP' },
    { id: 10, from: 'Account F', to: 'Account E', amount: 1800, currency: 'INR' },
    { id: 11, from: 'Account A', to: 'Account E', amount: 1100, currency: 'USD' },
    { id: 12, from: 'Account C', to: 'Account D', amount: 2500, currency: 'AUD' },
    { id: 13, from: 'Account B', to: 'Account A', amount: 1300, currency: 'GBP' },
    { id: 14, from: 'Account E', to: 'Account F', amount: 1600, currency: 'INR' },
    { id: 15, from: 'Account A', to: 'Account B', amount: 1400, currency: 'USD' }
  ]);

  const [newAmount, setNewAmount] = useState('');
  const [newCurrency, setNewCurrency] = useState('');
  const [selectedBudgetId, setSelectedBudgetId] = useState(null);

  const handleUpdateBudget = () => {
    // Update the selected budget
    const updatedBudgets = budgetData.map((budget) => {
      if (budget.id === selectedBudgetId) {
        return { ...budget, amount: newAmount, currency: newCurrency || budget.currency };
      }
      return budget;
    });

    // Update the state with the new budget data
    setBudgetData(updatedBudgets);
    setNewAmount('');
    setNewCurrency('');
  };

  const handleSelectBudget = (id) => {
    setSelectedBudgetId(id);
    const selectedBudget = budgetData.find((budget) => budget.id === id);
    if (selectedBudget) {
      setNewAmount(selectedBudget.amount);
      setNewCurrency(selectedBudget.currency);
    }
  };

  return (
    <div className="budget-container">
      <h2 className="form-title">Manage Budgets</h2>

      {/* Display budget list */}
      <div className="budget-list">
        <h3>Available Transactions:</h3>
        <ul>
          {budgetData.map((budget) => (
            <li key={budget.id}>
              {budget.from} → {budget.to}: {budget.currency} {budget.amount.toLocaleString()}
              <button onClick={() => handleSelectBudget(budget.id)} className="auth-button">
                Select
              </button>
            </li>
          ))}
        </ul>
      </div>

      {/* Form for updating selected budget */}
      {selectedBudgetId && (
        <div className="budget-update">
          <h4>Update Transaction for {budgetData.find((budget) => budget.id === selectedBudgetId)?.from} → {budgetData.find((budget) => budget.id === selectedBudgetId)?.to}</h4>
          <input
            type="number"
            value={newAmount}
            onChange={(e) => setNewAmount(e.target.value)}
            placeholder="Enter new amount"
            className="input-field"
          />
          <input
            type="text"
            value={newCurrency}
            onChange={(e) => setNewCurrency(e.target.value)}
            placeholder="Enter new currency"
            className="input-field"
          />
          <button onClick={handleUpdateBudget} className="auth-button">
            Update Budget
          </button>
        </div>
      )}
    </div>
  );
}

export default Budget;
