import React, { useState } from 'react';

function Transactions() {
  // Initial budgets data (for BRICS currencies)
  const [budgets, setBudgets] = useState([
    { currency: 'BRL', amount: 1000 },
    { currency: 'RUB', amount: 8000 },
    { currency: 'INR', amount: 15000 },
    { currency: 'CNY', amount: 2000 },
    { currency: 'ZAR', amount: 5000 },
  ]);

  // Transaction history (representing transactions between accounts)
  const [transactionHistory, setTransactionHistory] = useState([]);

  // Transaction form state
  const [transactionName, setTransactionName] = useState('');
  const [transactionAmount, setTransactionAmount] = useState('');
  const [transactionType, setTransactionType] = useState('');
  const [transactionCurrency, setTransactionCurrency] = useState('');
  const [transactionTo, setTransactionTo] = useState('');
  const [error, setError] = useState(null);

  const handleAddTransaction = () => {
    if (!transactionName || !transactionAmount || !transactionType || !transactionCurrency) {
      setError("Please fill in all fields");
      return;
    }

    const amount = parseFloat(transactionAmount);
    if (isNaN(amount) || amount <= 0) {
      setError("Please enter a valid amount.");
      return;
    }

    const updatedBudgets = [...budgets];
    const budgetIndex = updatedBudgets.findIndex((budget) => budget.currency === transactionCurrency);

    if (transactionType === 'deposit') {
      updatedBudgets[budgetIndex].amount += amount;
    } else if (transactionType === 'withdrawal') {
      if (updatedBudgets[budgetIndex].amount >= amount) {
        updatedBudgets[budgetIndex].amount -= amount;
      } else {
        setError("Insufficient funds for withdrawal.");
        return;
      }
    } else if (transactionType === 'transfer') {
      const transferToCurrency = transactionTo;
      const targetBudgetIndex = updatedBudgets.findIndex((budget) => budget.currency === transferToCurrency);

      if (targetBudgetIndex === -1) {
        setError("Invalid target currency.");
        return;
      }

      if (updatedBudgets[budgetIndex].amount >= amount) {
        updatedBudgets[budgetIndex].amount -= amount;
        updatedBudgets[targetBudgetIndex].amount += amount;

        // Record the transfer between accounts
        const newTransaction = {
          from: transactionName,
          to: transactionTo,
          amount: amount,
          type: 'transfer',
          currency: transactionCurrency,
          timestamp: new Date().toLocaleString(),
        };

        setTransactionHistory([...transactionHistory, newTransaction]);
        setError(null);
      } else {
        setError("Insufficient funds for transfer.");
        return;
      }
    }

    // Create a non-transfer transaction
    if (transactionType !== 'transfer') {
      const newTransaction = {
        name: transactionName,
        amount: amount,
        type: transactionType,
        currency: transactionCurrency,
        timestamp: new Date().toLocaleString(),
      };

      setTransactionHistory([...transactionHistory, newTransaction]);
    }

    // Update the budgets
    setBudgets(updatedBudgets);
    setTransactionName('');
    setTransactionAmount('');
    setTransactionType('');
    setTransactionCurrency('');
    setTransactionTo('');
  };

  return (
    <div className="transactions-container">
      <h2 className="form-title">Transactions</h2>
      {error && <div className="error-message">{error}</div>}

      {/* Transaction Form */}
      <div className="transaction-form">
        <input
          type="text"
          value={transactionName}
          onChange={(e) => setTransactionName(e.target.value)}
          placeholder="Account Name"
          className="input-field"
          required
        />
        <input
          type="number"
          value={transactionAmount}
          onChange={(e) => setTransactionAmount(e.target.value)}
          placeholder="Amount"
          className="input-field"
          required
        />
        <select
          value={transactionType}
          onChange={(e) => setTransactionType(e.target.value)}
          className="input-field"
          required
        >
          <option value="">Select Type</option>
          <option value="deposit">Deposit</option>
          <option value="withdrawal">Withdrawal</option>
          <option value="transfer">Transfer</option>
        </select>
        <select
          value={transactionCurrency}
          onChange={(e) => setTransactionCurrency(e.target.value)}
          className="input-field"
          required
        >
          <option value="">Select Currency</option>
          {budgets.map((budget) => (
            <option key={budget.currency} value={budget.currency}>
              {budget.currency}
            </option>
          ))}
        </select>
        {transactionType === 'transfer' && (
          <input
            type="text"
            value={transactionTo}
            onChange={(e) => setTransactionTo(e.target.value)}
            placeholder="Transfer To (Account Name)"
            className="input-field"
            required
          />
        )}
        <button onClick={handleAddTransaction} className="auth-button">Add Transaction</button>
      </div>

      {/* Transaction History */}
      <div className="transaction-history">
        <h3>Transaction History</h3>
        {transactionHistory.length === 0 ? (
          <p>No transactions found</p>
        ) : (
          <table>
            <thead>
              <tr>
                <th>From</th>
                <th>To</th>
                <th>Amount</th>
                <th>Type</th>
                <th>Currency</th>
                <th>Timestamp</th>
              </tr>
            </thead>
            <tbody>
              {transactionHistory.map((transaction, index) => (
                <tr key={index}>
                  <td>{transaction.from || transaction.name}</td>
                  <td>{transaction.to || 'N/A'}</td>
                  <td>{transaction.amount}</td>
                  <td>{transaction.type}</td>
                  <td>{transaction.currency}</td>
                  <td>{transaction.timestamp}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default Transactions;
