import React, { createContext, useState, useContext } from 'react';
import { loginUserAPI, loginEmployeeAPI } from './api';

const AuthContext = React.createContext(null);

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export function AuthProvider({ children }) {
  const [user, setUser] = useState(() => {
    const savedUser = localStorage.getItem('user');
    return savedUser ? JSON.parse(savedUser) : null;
  });

  const [token, setToken] = useState(() => {
    const savedToken = localStorage.getItem('token');
    return savedToken || null;
  });

  const login = async (credentials, isEmployee = false) => {
    try {
      let response;
      if (isEmployee) {
        response = await loginEmployeeAPI(credentials);
      } else {
        response = await loginUserAPI(credentials);
      }

      if (!response || !response.success || !response.data) {
        throw new Error('Invalid response from server');
      }

      const userData = {
        ...response.data,
        role: isEmployee ? 'employee' : 'user'
      };

      localStorage.setItem('user', JSON.stringify(userData));
      localStorage.setItem('token', response.data.token);

      setUser(userData);
      setToken(response.data.token);

      return { success: true, role: userData.role };
    } catch (error) {
      console.error('Login failed:', error);
      return { success: false, message: error.message };
    }
  };

  const logout = () => {
    localStorage.removeItem('user');
    localStorage.removeItem('token');
    setUser(null);
    setToken(null);
  };

  const value = {
    user,
    token,
    login,
    logout,
    isAuthenticated: !!token
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
}
