import React, { useState } from 'react';

function Budget() {
  // Initial data setup with BRICS countries and their budgets
  const [budgetData, setBudgetData] = useState([
    { id: 1, country: 'Brazil', amount: 20000, currency: 'BRL' },
    { id: 2, country: 'Russia', amount: 1500, currency: 'RUB' },
    { id: 3, country: 'India', amount: 250000, currency: 'INR' },
    { id: 4, country: 'China', amount: 30000, currency: 'CNY' },
    { id: 5, country: 'South Africa', amount: 12000, currency: 'ZAR' },
    { id: 6, country: 'Brazil', amount: 180000, currency: 'BRL' },
    { id: 7, country: 'Russia', amount: 13000, currency: 'RUB' },
    { id: 8, country: 'India', amount: 2700, currency: 'INR' },
    { id: 9, country: 'China', amount: 31000, currency: 'CNY' },
    { id: 10, country: 'South Africa', amount: 1100, currency: 'ZAR' },
    { id: 11, country: 'Brazil', amount: 220000, currency: 'BRL' },
    { id: 12, country: 'Russia', amount: 14000, currency: 'RUB' },
    { id: 13, country: 'India', amount: 29000, currency: 'INR' },
    { id: 14, country: 'China', amount: 32000, currency: 'CNY' },
    { id: 15, country: 'South Africa', amount: 12500, currency: 'ZAR' }
  ]);
  const [newBudget, setNewBudget] = useState('');
  const [newCurrency, setNewCurrency] = useState('');
  const [selectedBudgetId, setSelectedBudgetId] = useState(null);

  const handleUpdateBudget = () => {
    // Update the selected budget
    const updatedBudgets = budgetData.map((budget) => {
      if (budget.id === selectedBudgetId) {
        return { ...budget, amount: newBudget, currency: newCurrency || budget.currency };
      }
      return budget;
    });

    // Update the state with the new budget data
    setBudgetData(updatedBudgets);
    setNewBudget('');
    setNewCurrency('');
  };

  const handleSelectBudget = (id) => {
    setSelectedBudgetId(id);
    const selectedBudget = budgetData.find((budget) => budget.id === id);
    if (selectedBudget) {
      setNewBudget(selectedBudget.amount);
      setNewCurrency(selectedBudget.currency);
    }
  };

  return (
    <div className="budget-container">
      <h2 className="form-title">BRICS Budget Overview</h2>

      {/* Display budget list */}
      <div className="budget-list">
        <h3>Available Budgets by Country:</h3>
        <ul>
          {budgetData.map((budget) => (
            <li key={budget.id}>
              {budget.country} - {budget.currency} {budget.amount.toLocaleString()}
              <button onClick={() => handleSelectBudget(budget.id)}>Select</button>
            </li>
          ))}
        </ul>
      </div>

      {/* Form for updating selected budget */}
      {selectedBudgetId && (
        <div className="budget-update">
          <h4>Update Budget for {budgetData.find((budget) => budget.id === selectedBudgetId)?.country}</h4>
          <input
            type="number"
            value={newBudget}
            onChange={(e) => setNewBudget(e.target.value)}
            placeholder="Enter new amount"
            className="input-field"
          />
          <input
            type="text"
            value={newCurrency}
            onChange={(e) => setNewCurrency(e.target.value)}
            placeholder="Enter new currency"
            className="input-field"
          />
          <button onClick={handleUpdateBudget} className="auth-button">
            Update Budget
          </button>
        </div>
      )}
    </div>
  );
}

export default Budget;
