import React, { useState, useEffect } from 'react';
import { getBudget, updateBudget } from '../api';

function Budget() {
  const [budget, setBudget] = useState(null);
  const [newBudget, setNewBudget] = useState('');
  const [currency, setCurrency] = useState('');

  useEffect(() => {
    async function fetchBudget() {
      try {
        const response = await getBudget();
        setBudget(response.amount);
        setCurrency(response.currency);
      } catch (err) {
        console.error("Failed to fetch budget:", err);
        alert("Error fetching budget. Please check your authentication.");
      }
    }
    fetchBudget();
  }, []);

  const handleUpdateBudget = async () => {
    try {
      await updateBudget({ amount: newBudget });
      setBudget(newBudget);
      setNewBudget('');
    } catch (err) {
      console.error("Failed to update budget:", err);
      alert("Error updating budget. Please check your authentication.");
    }
  };

  return (
    <div className="budget-container">
      <h2 className="form-title">Budget</h2>
      {budget !== null ? (
        <p>Current Budget: {currency} {budget}</p>
      ) : (
        <p>Loading budget...</p>
      )}
      <div className="budget-update">
        <input
          type="number"
          value={newBudget}
          onChange={(e) => setNewBudget(e.target.value)}
          placeholder="Enter new amount"
          className="input-field"
        />
        <button onClick={handleUpdateBudget} className="auth-button">Update Budget</button>
      </div>
    </div>
  );
}

export default Budget;
