import React, { useState } from 'react';
import { useAuth } from '../AuthContext';
import { loginUserAPI, loginEmployeeAPI } from '../api';
import { useNavigate, Link } from 'react-router-dom';
import '../styles/global.css';

function Login() {
  const { login } = useAuth();
  const navigate = useNavigate();
  const [isEmployee, setIsEmployee] = useState(false);
  const [form, setForm] = useState({
    userName: '',
    accNumber: '',
    password: ''
  });
  const [error, setError] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm(prevForm => ({
      ...prevForm,
      [name]: value
    }));
  };

  const handleEmployeeToggle = (e) => {
    setIsEmployee(e.target.checked);
    // Clear account number when switching to employee login
    if (e.target.checked) {
      setForm(prev => ({
        ...prev,
        accNumber: ''
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const loginResponse = await login({
        username: form.userName,
        password: form.password,
        accNumber: form.accNumber
      }, isEmployee);

      if (loginResponse.success) {
        const from = location.state?.from?.pathname || 
          (isEmployee ? '/employee-dashboard' : '/user-dashboard');
        navigate(from, { replace: true });
      } else {
        setError(loginResponse.message);
      }
    } catch (err) {
      setError(err.message || 'Login failed. Please try again.');
    }
  };

  return (
    <div className="auth-container">
      <h2 className="form-title">{isEmployee ? 'Employee Login' : 'User Login'}</h2>

      <form onSubmit={handleSubmit} className="auth-form">
        <div className="form-group">
          <input
            type="text"
            name="userName"
            value={form.userName}
            onChange={handleChange}
            placeholder="Username"
            required
            autoComplete="username"
          />
        </div>

        {!isEmployee && (
          <div className="form-group">
            <input
              type="text"
              name="accNumber"
              value={form.accNumber}
              onChange={handleChange}
              placeholder="Account Number"
              required={!isEmployee}
              autoComplete="off"
            />
          </div>
        )}

        <div className="form-group">
          <input
            type="password"
            name="password"
            value={form.password}
            onChange={handleChange}
            placeholder={isEmployee ? "Employee Password" : "Password"}
            required
            autoComplete="current-password"
          />
        </div>

        <div className="checkbox-wrapper">
          <label>
            <input
              type="checkbox"
              checked={isEmployee}
              onChange={handleEmployeeToggle}
            />
            Employee Login
          </label>
        </div>

        {error && <div className="error-message">{error}</div>}

        <button type="submit" className="auth-button">
          Login
        </button>

        {!isEmployee && (
          <div className="auth-link">
            <p>Don't have an account? <Link to="/register">Register here</Link></p>
          </div>
        )}
      </form>
    </div>
  );
}

export default Login;
