// api.js
const BASE_URL = 'http://localhost:8080';
let csrfToken = null;
let authToken = null;

async function getCsrfToken() {
  if (!csrfToken) {
    const response = await fetch(`${BASE_URL}/csrf-token`, {
      credentials: 'include'
    });
    const data = await response.json();
    csrfToken = data.csrfToken;
  }
  return csrfToken;
}

async function getAuthToken() {
  if (!authToken) {
    // Fetch the auth token, e.g., during login
    const { data } = await loginUserAPI({ /* login credentials */ });
    authToken = data.token;
  }
  return authToken;
}

export async function registerUser(userData) {
  const token = await getCsrfToken();
  const response = await fetch(`${BASE_URL}/register`, {
    method: 'POST',
    headers: { 
      'Content-Type': 'application/json',
      'CSRF-Token': token
    },
    credentials: 'include',
    body: JSON.stringify(userData),
  });

  if (!response.ok) {
    const { error } = await response.json();
    throw new Error(`Registration failed: ${error}`);
  }

  return response.json();
}

export async function loginUserAPI(credentials) {
  const token = await getCsrfToken();
  const response = await fetch(`${BASE_URL}/login`, {
    method: 'POST',
    headers: { 
      'Content-Type': 'application/json',
      'CSRF-Token': token
    },
    credentials: 'include',
    body: JSON.stringify(credentials),
  });

  if (!response.ok) {
    const { error } = await response.json();
    throw new Error(`Invalid login: ${error}`);
  }

  const data = await response.json();
  if (!data.success || !data.data || !data.data.token) {
    throw new Error('Invalid response from server');
  }

  return data;
}

export async function getBudget() {
  try {
    const token = await getAuthToken();
    const response = await fetch('/api/budget', {
      method: 'POST', 
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
      },
    });

    if (!response.ok) {
      const { error } = await response.json();
      throw new Error(`Failed to fetch budget: ${error}`);
    }

    return response.json();
  } catch (err) {
    console.error('Error fetching budget:', err);
    throw err;
  }
}

